'use strict';const items_layout=document.querySelector("#items-layout");let firstCard,secondCard,hasFlippedCard=!1,lockBoard=!1;function renderItems(a){a.forEach(function(b){items_layout.innerHTML+=`<div class="card-item" data-name="${b.name}">
            <img class="front-face" src="${b.image_link}" />
            <img class="back-face" src="images/pineapple_bun_large.png" />
        </div>`});a=document.getElementsByClassName("card-item");for(let b=0;b<a.length;b++)a[b].onclick=function(){flipCard(this)}}function flipCard(a){lockBoard||a===firstCard||(a.classList.add("flip"),hasFlippedCard?(secondCard=a,checkForMatch()):(hasFlippedCard=!0,firstCard=a))}function checkForMatch(){firstCard.dataset.name===secondCard.dataset.name?disableMatchedCards():unflipCards()}
function disableMatchedCards(){firstCard.onclick=null;secondCard.onclick=null;setTimeout(()=>{alert("match!");resetBoard()},1E3)}function unflipCards(){lockBoard=!0;setTimeout(()=>{firstCard.classList.remove("flip");secondCard.classList.remove("flip");resetBoard()},1500)}function resetBoard(){[hasFlippedCard,lockBoard]=[!1,!1];[firstCard,secondCard]=[null,null]}function readJSONFile(a){fetch(a).then(b=>b.json()).then(b=>{totalItems=b.items;renderItems(totalItems)})}readJSONFile("seeders/data.json");